mapper.select:
select indicators_id as id,parent_Id,assessment_Type,indicators_name as title from assessment_indicators where assessment_year =#{year}
and assessment_Type < 2
UNION select detail_id as id ,assessment_id as parent_Id,4,assessment_criteria as title from assessment_indicators_detail

select DISTINCT a.assessment_year ,a.indicators_status,a.release_date,a.expiration_date,b.user_name as
createdUser
from assessment_indicators a left join user_manage b on a.CREATED_USER=b.user_id


        indicators_Status=#{assessmentPageVO.indicatorsStatus}


        assessment_Year=#{assessmentPageVO.assessmentYear}

SELECT count(a.detail_id) from assessment_indicators_detail a left join unit_assessment b on
a.detail_id=b.detail_id
where b.assessment_id is not null

    and a.indicators_id=#{id}


    and a.project_id=#{id}


    and a.task_id=#{id}


    and a.assessment_id=#{id}

select indicators_id as id,parent_Id,assessment_Type,indicators_name as title from assessment_indicators where assessment_Type > 0
and indicators_name like "%"#{content}"%"
UNION select b.report_id as id ,assessment_id as parent_Id,4,assessment_criteria as title from assessment_indicators_detail a right join indicator_report b on a.detail_id=b.detail_id where b.unit_Id =#{unitId}
and b.report_id=#{reportId}
and b.report_progress >=#{reportStatus}
and b.report_status=2
and (a.assessment_criteria like "%"#{content}"%" or a.instructions like "%"#{content}"%")
