mapper.select:
select count(*) from audit_log a
left join indicator_report b on a.report_id=b.report_id
where a.indicators_status=#{status} and unit_id=#{unitId}

    and report_progress=#{progress}

select a.report_id,a.UPDATED_TIME as assessmentDate,a.report_Progress,b.assessment_year,e.UNIT_NAME,c.USER_NAME as createdUser,c.PHONE_NUMBER,b.indicators_name,f.assessment_criteria from indicator_report a left join assessment_indicators b on a.indicators_id=b.indicators_id left join user_manage c on a.CREATED_USER=c.user_id left join unit_user d on a.CREATED_USER= d.user_id left join unit_manage e on e.UNIT_ID= a.unit_ID left join assessment_indicators_detail f on f.detail_id= a.detail_id left join audit_log g on g.report_id= a.report_id
where a.unit_id=#{unitId}
ORDER BY a.unit_id asc,a.CREATED_TIME desc

select COUNT(*) from indicator_report a left join assessment_indicators_detail b on a.detail_Id=b.detail_Id left join assessment_indicators c on c.indicators_id=b.task_id left join score_record d on a.report_id=d.report_id where a.unit_id=#{unitId} and c.assessment_year=#{year}
and c.unit_id =#{expertUnitId}
and (d.expert_status=0 or d.expert_status is null) and a.report_progress=2
and d.expert_status=1
and a.report_progress=3

select count(b.FILE_ID) from indicator_report a left join file_info b on a.report_id=b.biz_Id left join assessment_indicators_detail c on a.detail_id=c.detail_id left join assessment_indicators d on c.task_id=d.indicators_id where
a.report_Progress> 0
a.report_Progress> 1
and a.unit_Id= #{unitId}
and d.unit_Id= #{expertUnit}
